control.onCreate:
sender.currentPage = null;
sender.changePage = function(page) {
    //if (this.currentPage == page) return;
    if (this.currentPage) {
        if (this.currentPage.onDeactive) {
            this.currentPage.onDeactive();
        }
        ee.setVisible(this.currentPage, false);
    }

    this.currentPage = page;
    if (this.currentPage) {
        ee.setVisible(this.currentPage, true);
        ee.setVisible(sender, true);
        if (this.currentPage.onActive) {
            this.currentPage.onActive();
        }
    } else {
        ee.setVisible(sender, false);
    }
}

sender.v_fitInner = true;
sender.v_lastScrollTop = 0;
sender.v_items = new Array();
sender.c_title = g5.root.p_title.p1.c_title;
sender.v_needRefresh = false;
sender.v_status = "";

sender.v_pageIndex = 0;

sender.show = function(isHis) {
    if ((this.v_needRefresh) || (isHis != this.v_isHis)) {
        for (var i=0; i<this.v_items.length; i++) ee.removeControl(this.v_items[i]);
        this.v_items = new Array();
        this.v_satus = "";
        this.v_pageIndex = 0;
        this.v_lastScrollTop = 0;
        this.v_needRefresh = false;
    }
    this.v_isHis = isHis;
    this.parentNode.changePage(this);
}

sender.onActive = function() {
    if (this.v_isHis) {
        g5.pushHistory2("/PA/teamDel");
        ee.setText(this.c_title, "项目组列表(已删除)");
        g5.root.p_title.p1.c_btnIsHis.init(1);
    } else {
        g5.pushHistory2("/PA/team");
        ee.setText(this.c_title, "项目组列表");
        g5.root.p_title.p1.c_btnIsHis.init(0);
    }

    if (this.v_needRefresh) {
        for (var i=0; i<this.v_items.length; i++) ee.removeControl(this.v_items[i]);
        this.v_items = new Array();
        this.v_satus = "";
        this.v_pageIndex = 0;
        this.v_lastScrollTop = 0;
        this.v_needRefresh = false;
    }
    g5.root.p_title.changePage(g5.root.p_title.p1);
    setTimeout(function() {
        document.documentElement.scrollTop = sender.v_lastScrollTop;
    }, 1); 
    ee.tiledResizeV(this, 2);
    if (this.v_items.length<=0) {
        this.loadFromDB();
    } else {
        sender.processSize();
    }    
}

sender.onDeactive = function() {
    this.v_lastScrollTop = document.documentElement.scrollTop;
    g5.root.p_title.changePage(null);
}

sender.loadFromDB = function() {
    if (getTeamV2.busy) return;
    getTeamV2.pageIndex = sender.v_pageIndex;
    getTeamV2.isHis = this.v_isHis? "T":"F";
    getTeamV2.onComplete = function(dataset) {
        var root = sender;
        var cy = 0;
        var itemWidth = eb.getDivRect(root).w;
        var itemCount = 0;
        for (var i=0; i<dataset.rowCount; i++) {
            itemCount++;
            var item = createPanel(root, "mp1list_" + dataset.getData(0,i), "", 0, cy, itemWidth, 5);
            sender.loadRecord(item, dataset, i);
            item.v_mode = "";
            g5.renderTeam(item);
            cy += eb.getDivRect(item).h;
            sender.v_items.push(item);
        }
        eb.processDock();
        var rect = eb.getDivRectSetting(g5.root.p_title);
        ee.tiledV(sender, 0, rect.h, 500); 

        if (itemCount<=0) {
            sender.v_status = "noResult";
            var item = createPanel(sender, "mp1end", "", 0, cy, itemWidth, 60);
            addText(item, "没有更多了", "center", "center", "'Segoe UI', Meiryo, system-ui, -apple-system, BlinkMacSystemFont, sans-serif", 12, "a0a0a0" ,"bold");
            sender.v_items.push(item);
        } else {
            sender.v_status = "normal";
        }

        sender.processSize();
    }
    getTeamV2();
}

sender.processSize = function() {
    var rect = eb.getDivRectSetting(g5.root.p_title);
    if (sender.v_items.length>0) {
        var rect1 = eb.getDivRect(sender.v_items[sender.v_items.length-1]);
        ee.tiledV(sender, 0, rect.h, eb.getClientSize().h - eb.getDivRect(sender.v_items[sender.v_items.length-1]).h - rect.h + 2); 
    }
    ee.tiledResizeV(sender, 3);
}

sender.loadRecord = function(panel, dataset, index) {
    panel.v_teamInfo = new g5.TeamInfo();
    panel.v_teamInfo.loadFromDataset(dataset,0,index);
}

sender.loadMore = function() {
    if (this.v_status == "noResult") return;
    this.v_pageIndex++;
    this.loadFromDB();
}

sender.v_fitInner = true;
sender.v_lastScrollTop = 0;
sender.v_items = new Array();
sender.c_title = g5.root.p_title.p2.c_title;
sender.v_needRefresh = false;
sender.v_status = "";
sender.v_isHis = false;
sender.v_pageIndex = 0;

sender.showTeamTask = function(teamInfo, isHis) {
    if (parseInt(teamInfo)>0) {
        if ((this.v_teamInfo) &amp;&amp; (this.v_teamInfo.id == teamInfo)) {
            teamInfo = this.v_teamInfo;
        } else {
            var t = new g5.TeamInfo();
            t.id = teamInfo;
            teamInfo = t;
        }
    }
    if ((this.v_teamInfo != teamInfo) || (this.v_needRefresh) || (isHis != this.v_isHis)) {
        for (var i=0; i<this.v_items.length; i++) ee.removeControl(this.v_items[i]);
        this.v_items = new Array();
        this.v_satus = "";
        this.v_pageIndex = 0;
        this.v_lastScrollTop = 0;
        this.v_needRefresh = false;
    }
    this.v_teamInfo = teamInfo;
    this.v_isHis = isHis;
    this.parentNode.changePage(this);
}

sender.onActive = function() {
    if (this.v_isHis) {
        g5.pushHistory2("/PA/taskOld/" + escape(this.v_teamInfo.id));
        ee.setText(this.c_title, "(已完成)" + this.v_teamInfo.title);
        g5.root.p_title.p2.c_btnIsHis.init(1);
    } else {
        g5.pushHistory2("/PA/task/" + escape(this.v_teamInfo.id));
        ee.setText(this.c_title, this.v_teamInfo.title);
        g5.root.p_title.p2.c_btnIsHis.init(0);
    }
    if (!this.v_teamInfo.title) ee.setText(this.c_title, "任务列表");

    g5.root.p_title.changePage(g5.root.p_title.p2);
    setTimeout(function() {
        document.documentElement.scrollTop = sender.v_lastScrollTop;
    }, 1); 
    ee.tiledResizeV(this, 2);
    if (this.v_items.length<=0) {
        this.loadFromDB();
    } else {
        sender.processSize();
    }    
}

sender.onDeactive = function() {
    this.v_lastScrollTop = document.documentElement.scrollTop;
    g5.root.p_title.changePage(null);
}

sender.loadFromDB = function() {
    if (getTaskList.busy) return;
    getTaskList.pageIndex = sender.v_pageIndex;
    getTaskList.teamId = sender.v_teamInfo.id;
    getTaskList.isHis = this.v_isHis? "T": "F";
    getTaskList.onComplete = function(dataset) {
        var root = sender;
        var cy = 0;
        var itemWidth = eb.getDivRect(root).w;
        var itemCount = 0;
        for (var i=0; i<dataset.rowCount; i++) {
            itemCount++;
            var item = createPanel(root, "mp2list_" + dataset.getData(0,i), "", 0, cy, itemWidth, 5);
            sender.loadRecord(item, dataset, i);
            item.v_mode = "";
            g5.renderTask(item);
            cy += eb.getDivRect(item).h;
            sender.v_items.push(item);
        }
        eb.processDock();
        var rect = eb.getDivRectSetting(g5.root.p_title);
        ee.tiledV(sender, 0, rect.h, 500); 

        if (itemCount<=0) {
            sender.v_status = "noResult";
            var item = createPanel(sender, "mp2end", "", 0, cy, itemWidth, 60);
            addText(item, "没有更多了", "center", "center", "'Segoe UI', Meiryo, system-ui, -apple-system, BlinkMacSystemFont, sans-serif", 12, "a0a0a0" ,"bold");
            sender.v_items.push(item);
        } else {
            sender.v_status = "normal";
        }
        sender.processSize();
    }
    getTaskList();
}

sender.processSize = function() {
    var rect = eb.getDivRectSetting(g5.root.p_title);
    if (sender.v_items.length>0) {
        var rect1 = eb.getDivRect(sender.v_items[sender.v_items.length-1]);
        ee.tiledV(sender, 0, rect.h, eb.getClientSize().h - eb.getDivRect(sender.v_items[sender.v_items.length-1]).h - rect.h + 2); 
    }
    ee.tiledResizeV(sender, 3);
}

