sender.init = function(clear) {
    if (clear) this.c_edit.setEditText("");
    this.c_photos.v_photos = new Array();
    //g5.refreshPhoto(this.c_photos);
    this.check();
    this.c_cir.refresh(0);
    ee.setText(this.c_cirText, "");    
}

sender.onActive = function() {
    g5.pushHistory2("/PA/composeChat/" + escape(this.v_taskId));
    document.documentElement.scrollTop = 0;
    ee.tiledResizeV(this, 2);
}

sender.onDeactive = function() {
    g5.root.p_title.changePage(null);
}

sender.check = function() {
    this.c_button.setEnabled(
        ((this.c_edit.getEditText().trim().length>0) &amp;&amp;(this.c_edit.getEditText().trim().length<=800))
    );    
}

sender.showLast = function() {
    this.c_p1.resize();
    g5.changeP2(30);
}

sender.showNew = function(taskId) {
    this.v_taskId = taskId;
    this.c_p1.resize();
    this.init(true);
    ee.setText(this.c_title, "发表动态");
    this.c_button.setText("发表", 0);
    this.c_button.setText("发表", 1);
    this.v_lastOp = "new";
    this.onComplete = function() {
        chatNewV2.taskId = sender.v_taskId;
        chatNewV2.content = sender.c_edit.getEditText();
        chatNewV2.autoComplete = "F";
        chatNewV2.onComplete = function(taskId) {
            sender.v_lastOp = "";
            g5.root.mainDiv.p1.p2.v_needRefresh = true;
            g5.root.mainDiv.p1.p3.v_needRefresh = true;
            history.go(-1);
        }
        chatNewV2();
    }
    this.parentNode.changePage(this);
}

sender.showComplete = function(taskId) {
    this.v_taskId = taskId;
    this.c_p1.resize();
    this.init(true);
    ee.setText(this.c_title, "发表动态并完成任务");
    this.c_button.setText("完成", 0);
    this.c_button.setText("完成", 1);
    this.v_lastOp = "new";
    this.onComplete = function() {
        chatNewV2.taskId = sender.v_taskId;
        chatNewV2.content = sender.c_edit.getEditText();
        chatNewV2.autoComplete = "T";
        chatNewV2.onComplete = function(taskId) {
            sender.v_lastOp = "";
            g5.root.mainDiv.p1.p1.v_needRefresh = true;
            g5.root.mainDiv.p1.p2.v_needRefresh = true;
            g5.root.mainDiv.p1.p3.v_needRefresh = true;
            history.go(-1);
        }
        chatNewV2();
    }
    this.parentNode.changePage(this);
}

