sender.showEditTask = function(taskInfo) {
    this.c_p1.resize();
    this.init(true);
    this.v_taskInfo = taskInfo;
    ee.setText(this.c_title, "编辑任务");
    this.c_button.setText("确定",0);
    this.c_button.setText("确定",1);
    ee.setText(this.c_editTitle, "在这里输入任务名称");
    ee.setText(this.c_edit.c_label, "在这里输入任务描述");
    ee.setEditText(sender.c_editTitle, this.v_taskInfo.taskName);
    sender.c_edit.setEditText(this.v_taskInfo.note);
    this.v_lastOp = "showEditTeam";
    this.onComplete = function() {
        modifyTaskInfo.taskId = sender.v_taskInfo.id;
        modifyTaskInfo.title = ee.getEditText(sender.c_editTitle);
        modifyTaskInfo.note = sender.c_edit.getEditText();
        modifyTaskInfo.onComplete = function(taskId) {
            sender.v_lastOp = "";
            g5.root.mainDiv.p1.p1.v_needRefresh = true;
            history.go(-1);
        }
        modifyTaskInfo();
    }
    g5.pushHistory2("/PA/editTeam");
    this.parentNode.changePage(this);
}

sender.showNewTask = function(teamId) {
    this.v_teamId = teamId;
    this.c_p1.resize();
    this.init(true);
    ee.setText(this.c_title, "创建新的任务");
    this.c_button.setText("创建",0);
    this.c_button.setText("创建",1);
    ee.setText(this.c_editTitle, "在这里输任务名称");
    ee.setText(this.c_edit.c_label, "在这输入任务描述");
    this.v_lastOp = "showNewTask";
    this.onComplete = function() {
        taskNewV2.teamId = sender.v_teamId;
        taskNewV2.taskTitle = ee.getEditText(sender.c_editTitle);
        taskNewV2.taskNote = sender.c_edit.getEditText();
        taskNewV2.onComplete = function(taskId) {
            sender.v_lastOp = "";
            g5.root.mainDiv.p1.p1.v_needRefresh = true;
            g5.root.mainDiv.p1.p2.v_needRefresh = true;
            history.go(-1);
        }
        taskNewV2();
    }
    g5.pushHistory2("/PA/newTask/" + escape(this.v_teamId));
    this.parentNode.changePage(this);
}

