sender.currentPage = null;
sender.changePage = function(page) {
    if (this.currentPage == page) return;
    if (this.currentPage) {
        if (this.currentPage.onDeactive) {
            this.currentPage.onDeactive();
        }
        ee.setVisible(this.currentPage, false);
    }

    this.currentPage = page;
    if (this.currentPage) {
        ee.setVisible(this.currentPage, true);
        ee.setVisible(sender, true);
        ee.tiledResizeV(sender,1);
        if (this.currentPage.onActive) {
            this.currentPage.onActive();
        }
    } else {
        ee.setVisible(sender, false);
    }
}

