control.onUpdate:
if (text == "") {
    ee.setText(sender, text);
    return;
}
var hour,time = "";
if (text.split(",").length==2) {
    time = text.split(",")[0];
    hour = parseInt(text.split(",")[1]);
} else {
    hour = parseInt(text);
}
if (hour<24) {
    text = hour + "小时";
} else if (hour==24) {
    text = "1天";
} else if (hour<48) {
    text = "1天零" + (hour-24) + "小时";
} else {
    text = parseInt(hour/24) + "天";
}
if (time.length>0) {
    text = time + "</br>" + text;
}
ee.setText(sender, text);

