taskSetItem.script2:
var token = eio.readString8();
var history = eio.readInt32();
var teamId = eio.readInt32();
var sql,sqlResult;

sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("系统繁忙请稍候再试");
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}
var userId = db.getString(sqlResult, "id");

sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("系统繁忙请稍候再试");
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}
var userId = db.getString(sqlResult, "id");

if (history == 1) {
    sql = "select taskName, convert(varchar(100), closeDatetime, 23) " +
        "+ ',' + cast(datediff(hour, regDatetime, closeDatetime) as varchar(10)) as [time], id, 1 as chatCount, 1 as readCount " +
        "from taskInfo where status = 'CLS' and teamId = " + teamId + " order by closeDatetime desc ";
} else {
//    sql = "select taskName, datediff(hour, regDatetime, getdate()) as [time], id " +
//        "from taskInfo where status = 'NEW' and teamId = " + teamId + " order by regDatetime desc ";
    sql = 
        "SELECT TOP (100) PERCENT dbo.taskInfo.taskName, DATEDIFF(hour, " +  
        "      dbo.taskInfo.regDatetime, GETDATE()) AS time, dbo.taskInfo.id, " +  
        "      COUNT(dbo.taskChat.id) AS chatCount, ISNULL(t1.readCount, - 1) AS readCount " + 
        "FROM (SELECT id, regDatetime, userId, type, taskId, readCount " + 
        "        FROM dbo.unread " + 
        "        WHERE (userId = " + userId + ")) AS t1 RIGHT OUTER JOIN " + 
        "      dbo.taskInfo ON t1.taskId = dbo.taskInfo.id LEFT OUTER JOIN " + 
        "      dbo.taskChat ON dbo.taskInfo.id = dbo.taskChat.taskId " + 
        "WHERE (dbo.taskInfo.status = 'NEW') AND (dbo.taskInfo.teamId = " + teamId + ") " + 
        "GROUP BY dbo.taskInfo.taskName, dbo.taskInfo.regDatetime, dbo.taskInfo.id, " +  
        "      t1.readCount " + 
        "ORDER BY time ";
}
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"taskName,time,id,chatCount,readCount",0,1000);

