this.loadFromDataset = function(ds, x, idx) {
    this.id = ds.getData(x,idx); x++;
    this.status = ds.getData(x,idx); x++;
    this.regDatetimeUTC = ds.getData(x,idx); x++;
    this.regDatetime = g5.getUtcTime(this.regDatetimeUTC);
    this.lastAccessTimeUTC = ds.getData(x,idx); x++;
    this.lastAccessTime = g5.getUtcTime(this.lastAccessTimeUTC);
    this.title = ds.getData(x,idx); x++;
    this.note = ds.getData(x,idx); x++;
    this.newTaskCount = ds.getData(x,idx); x++;
    this.creator = new g5.UserInfo();
    this.creator.loadFromDataset(ds, x, idx); x += this.creator.size;
    this.owner = new g5.UserInfo();
    this.owner.loadFromDataset(ds, x, idx); x += this.owner.size;
}
this.setDataset = function(ds, x, idx) {
    ds.setData(x,idx,this.id); x++;
    ds.setData(x,idx,this.status); x++;
    ds.setData(x,idx,this.regDatetimeUTC); x++;
    ds.setData(x,idx,this.lastAccessTimeUTC); x++;
    ds.setData(x,idx,this.title); x++;
    ds.setData(x,idx,this.note); x++;
