    ds.setData(x,idx,this.id); x++;
    ds.setData(x,idx,this.regDatetimeUTC); x++;
    ds.setData(x,idx,this.taskId); x++;
    ds.setData(x,idx,this.content); x++;
    this.poster.setDataset(ds, x, idx); x += this.poster.size;
}
