//{script type="text/javascript" src="http://res.wx.qq.com/open/js/jweixin-1.0.0.js">{/script> 

if ("scrollRestoration" in history) {
    history.scrollRestoration = "manual";
}

var sUserAgent = navigator.userAgent;
if (sUserAgent.indexOf('Android') > -1 || sUserAgent.indexOf('iPhone') > -1 || sUserAgent.indexOf('iPod') > -1 || sUserAgent.indexOf('Symbian') > -1) {
    g5.clientType = "mobile";
    g5.isMobile = true;
} else {
    g5.clientType = "pc";
    g5.isMobile = false;
}
if (sUserAgent.indexOf("iPhone") > -1 || sUserAgent.indexOf("iPad") > -1 || sUserAgent.indexOf("iPod") > -1) {
    g5.isIos = true;
} else {
    g5.isIos = false;
}

