control.onUpdate:
var unreadCount = 
    parseInt(g5.task1.getData(3, sender.rowIndex)) - 
    parseInt(g5.task1.getData(4, sender.rowIndex));

if (unreadCount > 0) {
    text += "<b><font color='#d00000'>(" + unreadCount + "未读)</font></b>";
}   
ee.setText(sender, text);

control.onClick:
ee.showAni(ee.getControl("panel16"));
g5.taskId = g5.task1.getData(2, sender.rowIndex);
g5.unreadCount =                  c
    parseInt(g5.task1.getData(3, sender.rowIndex)) - 
    parseInt(g5.task1.getData(4, sender.rowIndex));
taskDetail();

control.onUpdate:
if (text == "") {
    ee.setText(sender, text);
    return;
}
var hour,time = "";
if (text.split(",").length==2) {
    time = text.split(",")[0];
    hour = parseInt(text.split(",")[1]);
} else {
    hour = parseInt(text);
}
if (hour<24) {
    text = hour + "小时";
} else if (hour==24) {
    text = "1天";
} else if (hour<48) {
    text = "1天零" + (hour-24) + "小时";
} else {
    text = parseInt(hour/24) + "天";
}
if (time.length>0) {
    text = time + "</br>" + text;
}
ee.setText(sender, text);

