item.functionCode:
var uSize = new g5.UserInfo();
uSize = uSize.size;
this.size = 6 + uSize + uSize;
this.loadFromDataset = function(ds, x, idx) {
    this.id = ds.getData(x,idx); x++;
    this.status = ds.getData(x,idx); x++;
    this.regDatetimeUTC = ds.getData(x,idx); x++;
    this.regDatetime = g5.getUtcTime(this.regDatetimeUTC, true);
    this.lastAccessTimeUTC = ds.getData(x,idx); x++;
    this.lastAccessTime = g5.getUtcTime(this.lastAccessTimeUTC, true);
    this.title = ds.getData(x,idx); x++;
    this.note = ds.getData(x,idx); x++;
    this.creator = new g5.UserInfo();
    this.creator.loadFromDataset(ds, x, idx); x += this.creator.size;
    this.owner = new g5.UserInfo();
    this.owner.loadFromDataset(ds, x, idx); x += this.owner.size;
}
this.setDataset = function(ds, x, idx) {
    ds.setData(x,idx,this.id); x++;
    ds.setData(x,idx,this.status); x++;
    ds.setData(x,idx,this.regDatetimeUTC); x++;
    ds.setData(x,idx,this.lastAccessTimeUTC); x++;
    ds.setData(x,idx,this.title); x++;
    ds.setData(x,idx,this.note); x++;
    this.creator.setDataset(ds, x, idx); x += this.creator.size;
    this.owner.setDataset(ds, x, idx); x += this.owner.size;
}
this.clone = function() {
    var r = new g5.TeamInfo();
    r.id = this.id;
    r.status = this.status;
    r.regDatetime = this.regDatetime;
    r.regDatetimeUTC = this.regDatetimeUTC;
    r.lastAccessTime = this.lastAccessTime;
    r.lastAccessTimeUTC = this.lastAccessTimeUTC;
    r.title = this.title;
    r.note = this.note;
    r.creator = this.creator.clone();
    r.owner = this.owner.clone();
    return r;
}

this.size = 11;
this.loadFromDataset = function(ds, x, idx) {
    this.id = ds.getData(x,idx); x++;
    this.status = ds.getData(x,idx); x++;
    this.regDatetimeUTC = ds.getData(x,idx);
    this.regDatetime = g5.getUtcTime(this.regDatetimeUTC, true);
    this.lastAccessTimeUTC = ds.getData(x,idx); x++;
    this.lastAccessTime = g5.getUtcTime(this.lastAccessTimeUTC, true);
    this.userName = ds.getData(x,idx); x++;
    this.mobile = ds.getData(x,idx); x++;
    this.nickName = ds.getData(x,idx); x++; 
    this.note = ds.getData(x,idx); x++;
    this.photo = ds.getData(x,idx); x++;
    this.titleBg = ds.getData(x,idx); x++;
    this.email = ds.getData(x,idx); x++;
}
this.setDataset = function(ds, x, idx) {
    ds.setData(x,idx,this.id); x++;
    ds.setData(x,idx,this.status); x++;
    ds.setData(x,idx,this.regDatetimeUTC); x++;
    ds.setData(x,idx,this.lastAccessTimeUTC); x++;
    ds.setData(x,idx,this.userName); x++;
    ds.setData(x,idx,this.mobile); x++;
    ds.setData(x,idx,this.nickName); x++; 
    ds.setData(x,idx,this.note); x++;
    ds.setData(x,idx,this.photo); x++;
    ds.setData(x,idx,this.titleBg); x++;
    ds.setData(x6,idx,this.email); x++;
}
this.clone = function() {
    var r = new g5.UserInfo();
    r.id = this.id;
    r.status = this.status;
    r.regDatetime = this.regDatetime;
    r.regDatetimeUTC = this.regDatetimeUTC;
    r.lastAccessTime = this.lastAccessTime;
    r.lastAccessTimeUTC = this.lastAccessTimeUTC;
    r.userName = this.userName;
    r.mobile = this.mobile;
    r.nickName = this.nickName;
    r.note = this.note;
    r.photo = this.photo;
    r.titleBg = this.titleBg;
    r.email = this.email;
    return r;
}

this.color1 = color1;
this.color2 = color2
this.color3 = color3;
this.text1 = text1;
this.text2 = text2;
this.text3 = text3;
this.tColor1 = tColor1;
this.tColor2 = tColor2;
this.tColor3 = tColor3;
this.bdColor = bdColor;

sender.v_style = v_style;

sender.init = function(index) {
    this.v_index = index;
    this.style.backgroundColor = this.v_style[index].color1;
    this.setText(this.v_style[index].text1);
    this.setTextColor(this.v_style[index].tColor1);
    if (this.v_style[index].bdColor=="") {
        this.style.borderWidth = "0px";
        if (this.label) eb.setDivRect(this.label.parentNode,0,0,null,null);
    } else {
        this.style.boxSizing="border-box";
        this.style.borderWidth = "1px";
        this.style.borderColor = this.v_style[index].bdColor;
        this.style.borderStyle = "solid";
        if (this.label) eb.setDivRect(this.label.parentNode,-1,-1,null,null);
    }
    //this.style.borderRadius = parseInt(eb.getDivRect(this).h/2) + "px";
    if (this.c_cover) ee.removeControl(this.c_cover);
    this.c_cover = createPanel(this, "", "#00000001", 0, 0, 1, 1);
    eb.setDock(this.c_cover,"fill","fill");
}

sender.setText = function(text) {
    var index = this.v_index;
    if ((this.v_style[index].text1.length>0) || (this.v_style[index].text2.length>0) || (this.v_style[index].text3.length>0)) {
        ee.setText(this,text);
    }
}

sender.setTextColor = function(color) {
    var index = this.v_index;
    if ((this.v_style[index].tColor1.length>0) || (this.v_style[index].tColor2.length>0) || (this.v_style[index].tColor3.length>0)) {
        ee.setTextColor(this, color);
    }
}

ee.setOnMouseEnter(sender, function(sender,event) { 
    var s =sender.v_style[sender.v_index];
    sender.style.backgroundColor = s.color2;
    this.setText(s.text2);
    this.setTextColor(s.tColor2);
});
ee.setOnMouseLeave(sender, function(sender,event) { 
    var s =sender.v_style[sender.v_index];
    sender.style.backgroundColor = s.color1;
    this.setText(s.text1);
    this.setTextColor(s.tColor1);
});
sender.eeMouseDown = function(sender,event,x,y) {
    var s =sender.v_style[sender.v_index];
    sender.style.backgroundColor = s.color3;
    this.setText(s.text3);
    this.setTextColor(s.tColor3);
}
sender.eeMouseUp = function(sender,event,x,y) {
    var s =sender.v_style[sender.v_index];
    if ("ontouchstart" in window) {
        sender.style.backgroundColor = s.color1;
        this.setText(s.text1);
        this.setTextColor(s.tColor1);
    } else {
        sender.style.backgroundColor = s.color2;
        this.setText(s.text2);
        this.setTextColor(s.tColor2);
    }
}
sender.eeOnClickCancel = function(sender,event) {
    var s =sender.v_style[sender.v_index];
    sender.style.backgroundColor = s.color1;
    this.setText(s.text1);
    this.setTextColor(s.tColor1);
}
eb.initMouseEvent(sender);

sender.init(0);

if (g5.currentPage == page) return;
if (g5.currentPage) {
    if (g5.currentPage.onDeactive) {
        g5.currentPage.onDeactive();
    }
    ee.setVisible(g5.currentPage, false);
}

g5.currentPage = page;
if (g5.currentPage) {
    if (g5.currentPage.onActive) {
        g5.currentPage.onActive();
    }
    ee.setVisible(g5.currentPage, true);
}

